import React from 'react';
import { Box, Container, Grid, Button, Input, Heading, Text } from 'theme-ui';
import { Link } from 'components/link';

import ContactForm from 'components/contact-form';

import img1 from 'assets/partner-1-1.png';
import img2 from 'assets/partner-1-2.png';
import img3 from 'assets/partner-1-3.png';

import bannerImg from 'assets/banner-image-1-1.png';

const Contact = () => {
  return (
    <Box sx={styles.contact} id="contact">
      <Container sx={styles.container}>
        <Grid sx={styles.grid}>
          <Box sx={styles.content}>
            <Heading as="h3">
                Reach Out
            </Heading>
            <Text as="p">
                Don't like forms? Send us an email
            </Text>
            <ContactForm/>
          </Box>
        </Grid>
      </Container>
    </Box>
  );
};

export default Contact;

const styles = {
    contact_us: {
        pt: ['140px', '145px', '155px', '170px', null, null, '180px', '215px'],
        pb: [2, null, 0, null, 2, 0, null, 5],
        position: 'relative',
        zIndex: 2,
        '&::before': {
          position: 'absolute',
          content: '""',
          bottom: 6,
          left: 0,
          height: '100%',
          width: '100%',
          zIndex: -1,
          backgroundImage: `url(${ShapeLeft})`,
          backgroundRepeat: `no-repeat`,
          backgroundPosition: 'bottom left',
          backgroundSize: '36%',
        },
        '&::after': {
          position: 'absolute',
          content: '""',
          bottom: '40px',
          right: 0,
          height: '100%',
          width: '100%',
          zIndex: -1,
          backgroundImage: `url(${ShapeRight})`,
          backgroundRepeat: `no-repeat`,
          backgroundPosition: 'bottom right',
          backgroundSize: '32%',
        },
        container: {
          minHeight: 'inherit',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        },
        contentBox: {
          width: ['100%', '90%', '535px', null, '57%', '60%', '68%', '60%'],
          mx: 'auto',
          textAlign: 'center',
          mb: ['40px', null, null, null, null, 7],
        },
        imageBox: {
          justifyContent: 'center',
          textAlign: 'center',
          display: 'inline-flex',
          mb: [0, null, -6, null, null, '-40px', null, -3],
          img: {
            position: 'relative',
            height: [245, 'auto'],
          },
        },
      },
  };
