import React from 'react';
import { Box, Container, Grid, Button, Input, Heading, Text } from 'theme-ui';
import { Link } from 'components/link';

import ContactForm from 'components/contact-form';

import img1 from 'assets/partner-1-1.png';
import img2 from 'assets/partner-1-2.png';
import img3 from 'assets/partner-1-3.png';

import bannerImg from 'assets/banner-image-1-1.png';

const Contact = () => {
  return (
    <Box sx={styles.contact} id="contact">
      <Container sx={styles.container}>
        <Grid sx={styles.grid}>
          <Box sx={styles.content}>
            <Heading as="h3">
                Reach Out
            </Heading>
            <Text as="p">
                Don't like forms? Send us an email
            </Text>
            <ContactForm/>
          </Box>
        </Grid>
      </Container>
    </Box>
  );
};

export default Contact;

const styles = {
    Contact: {
      pt: ['80px', null, null, null, null, null, '140px'],
      '.service-card:nth-of-type(2)': {
        '.service-icon': {
          backgroundImage:
            'linear-gradient(320.89deg, #25D9D9 10.83%, rgba(37, 217, 217, 0.5) 88.7%)',
        },
      },
      '.service-card:nth-of-type(3)': {
        '.service-icon': {
          backgroundImage:
            'linear-gradient(319.4deg, #0898E7 5.17%, rgba(8, 152, 231, 0.5) 94.34%)',
        },
      },
      '.service-card:nth-of-type(4)': {
        '.service-icon': {
          backgroundImage:
            'linear-gradient(322.63deg, #FF9066 9.94%, rgba(255, 144, 102, 0.5) 91.14%)',
        },
      },
    },
    blockTitle: {
      textAlign: 'center',
    },
    grid: {
      display: 'grid',
      gridGap: ['30px', null, null, null, null, '60px'],
      gridTemplateColumns: [
        '1fr',
        null,
        null,
        '1fr 1fr',
        null,
        '1fr 1fr 1fr 1fr',
      ],
    },
    icon: {
      display: 'flex',
      ml: 'auto',
      mr: 'auto',
      width: ['80px', null, null, '110px'],
      height: ['80px', null, null, '110px'],
      justifyContent: 'center',
      alignItems: 'center',
      borderRadius: ['20px', null, null, '40px'],
      backgroundImage:
        'linear-gradient(323.91deg, #FFCC40 7.09%, rgba(255, 204, 64, 0.5) 88.82%)',
    },
    serviceCard: {
      textAlign: 'center',
      h3: {
        margin: 0,
        fontSize: ['18px', null, null, 3],
        fontWeight: 'bold',
        lineHeight: 1,
        color: 'black',
        mt: ['30px', null, null],
        mb: ['20px', null, null],
      },
      p: {
        margin: 0,
        fontSize: [0, null, null, '15px'],
        color: 'heading_secondary',
        width: '100%',
        maxWidth: [null, null, null, null, '84%', '100%'],
        mx: [null, null, null, null, 'auto', '0'],
      },
    },
  };
